package JavaSessions;

public class Primitive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = 21;
		byte c = -22;
		System.out.println(b+c);
		
		short d = -327;
		short e = 767;
		System.out.println(d-e);
		
		int f = 33003;
		short g = 20000;
		System.out.println(f+g);
		
		long h = 700046;
		short i = -30000;
		System.out.println(h+i);
		
		float j = 12.0000004f;
		float k = 0.6464634f;
		System.out.println(j-k);
		
		double l = 564.454648431324;
		float m = 56.002f;
		System.out.println(l+m);
		
		float n = 0.0046f;
		float o = 55.12f;
		System.out.println(n-o);
		
		String p = "Hello World"; //non-primitive
				System.out.println(p);
				
		char q = '2';
		char r = '5';
		System.out.println(q+r); //as per Ascii table 2 = 10 and 5 = 101
		
		String s = "1st Session"; // non-primitive 
		System.out.println(s);
		
		
	     
		
		
		

	}

}
